mapper.select:
select

from idx_value_manual


    and created_date  >=  CAST( #{startDate,jdbcType=TIMESTAMP} AS DATE )


    AND created_date  <=  CAST( #{endDate,jdbcType=TIMESTAMP} AS DATE )


    AND created_date = CAST( #{createDate,jdbcType=TIMESTAMP} AS DATE )


    AND index_id = #{indexId,jdbcType=VARCHAR}


    AND index_id IN

      #{item,jdbcType=VARCHAR}



    AND label = #{label,jdbcType=VARCHAR}


    AND org_code = #{orgCode,jdbcType=VARCHAR}


    and product_code=#{productCode,jdbcType=VARCHAR}


    and sub_product_code=#{subProductCode,jdbcType=VARCHAR}


    AND

      reg_channel=#{key,jdbcType=VARCHAR}

        AND sub_channel IN

          #{item,jdbcType=VARCHAR}





order by created_date desc
